```csharp
using System;
using System.Runtime.InteropServices;

namespace MinimizeConsoleApp
{
    class Program
    {
        [DllImport("User32.dll", SetLastError = true)]
        static extern bool ShowWindow(IntPtr hWnd, int nCmdShow);

        const int SW_HIDE = 0x700; // Hide the window

        static void Main()
        {
            Process.Start("C:\\Path\\To\\Your\\Program.exe");
            IntPtr handle = Process.GetProcessesByName("YourApplication")[0].MainWindowHandle;
            ShowWindow(handle, SW_HIDE);
        }
    }
}
```

